  package com.rsa.utils;

  import java.security.Key;
  import java.security.KeyFactory;
  import java.security.KeyPair;
  import java.security.KeyPairGenerator;
  import java.security.NoSuchAlgorithmException;
  import java.security.spec.PKCS8EncodedKeySpec;
  import java.security.spec.X509EncodedKeySpec;
  import java.util.HashMap;
  import java.util.Map;

  import javax.crypto.Cipher;

  import org.apache.commons.codec.binary.Base64;

  public class RSAUtils {

  	public static final String KEY_ALGORITHM = "RSA";

  	public static final String PUBLIC_KEY = "RSAPublicKey";

  	public static final String PRIVATE_KEY = "RSAPrivateKey";

  	public static byte[] decryptBASE64(String key) {
          return Base64.decodeBase64(key);
      }

      public static String encryptBASE64(byte[] bytes) {
          return Base64.encodeBase64String(bytes);
      }

  	/**
  	 * 生成密钥
  	 * @return
  	 * @throws Exception
  	 */
  	public static Map<String, Key> generateKey(){
  		Map<String, Key> keyMap = new HashMap<String,Key>(2);

  		try {
  			KeyPairGenerator keyPairGenerator = KeyPairGenerator.getInstance(KEY_ALGORITHM);
  			keyPairGenerator.initialize(1024);
  			KeyPair keyPair = keyPairGenerator.generateKeyPair();
  		 	keyMap.put(PUBLIC_KEY, keyPair.getPublic());// 公钥
  	        keyMap.put(PRIVATE_KEY, keyPair.getPrivate());// 私钥
  		}catch (NoSuchAlgorithmException e) {
  			e.printStackTrace();
  		}

  		return keyMap;
  	}

      /**
       * 取得公钥
       * @param keyMap
       * @return
       * @throws Exception
       */
      public static String getPublicKey(Map<String, Key> keyMap) {
          Key key = keyMap.get(PUBLIC_KEY);
          return encryptBASE64(key.getEncoded());
      }

      /**
       * 取得私钥
       * @param keyMap
       * @return
       * @throws Exception
       */
      public static String getPrivateKey(Map<String, Key> keyMap) {
          Key key = (Key) keyMap.get(PRIVATE_KEY);
          return encryptBASE64(key.getEncoded());
      }

      /**
       * 解密<br>
       * @param data
       * @param key
       * @return
       * @throws Exception
       */
      public static String decrypt(String data, String key) {
          return decrypt(decryptBASE64(data), key);
      }

  	/**
  	 * 解密
  	 * @param data
  	 * @param key
  	 * @return
  	 * @throws Exception
  	 */
  	public static String decrypt(byte[] data, String key){
  		String result = null;

  		byte[] keyBytes = decryptBASE64(key);
  		try {
  			PKCS8EncodedKeySpec pkcs8KeySpec = new PKCS8EncodedKeySpec(keyBytes);
  			KeyFactory keyFactory= KeyFactory.getInstance(KEY_ALGORITHM);
  			Key privateKey = keyFactory.generatePrivate(pkcs8KeySpec);

  			Cipher cipher = Cipher.getInstance(keyFactory.getAlgorithm());
  			cipher.init(Cipher.DECRYPT_MODE, privateKey);
  			byte[] decryptData = cipher.doFinal(data);
  			result = new String(decryptData);

  		} catch (Exception e) {
  			e.printStackTrace();
  		}

  		return result;
  	}

  	/**
       * 加密
       * @param data
       * @param key
       * @return
       * @throws Exception
       */
      public static byte[] encrypt(String data, String key) {
      	byte[] result = null;

          byte[] keyBytes = decryptBASE64(key);
          try{
          	X509EncodedKeySpec x509KeySpec = new X509EncodedKeySpec(keyBytes);
          	KeyFactory keyFactory = KeyFactory.getInstance(KEY_ALGORITHM);
          	Key publicKey = keyFactory.generatePublic(x509KeySpec);

          	Cipher cipher = Cipher.getInstance(keyFactory.getAlgorithm());
          	cipher.init(Cipher.ENCRYPT_MODE, publicKey);
          	result =  cipher.doFinal(data.getBytes());
          } catch(Exception e){
          	e.printStackTrace();
          }

          return result;
      }

  }
```

